inputs.where:
@and(
    equals(item()?['_crb76_produto_value'], outputs('ProdutoMáquina')?['body/crb76_produtoid']),
    greaterOrEquals(outputs('Obtendo_Recolhimento')?['body/lt_data'], item()?['crb76_datainicio']),
    lessOrEquals(
        outputs('Obtendo_Recolhimento')?['body/lt_data'],
        if(empty(item()?['lt_datafim']), utcNow(), item()?['lt_datafim'])
    ),
    equals(item()?['_lt_loja_value'], outputs('ObtendoLoja1')?['body/cre0a_lojaid']),
    not(equals(item()?['crb76_preco'], 0))
)
